        for (int i = 0; i < selectedRowIndices.length; i++) {
            VariableSummary variableSummary = variableSearchTableModel.getVariableSummary(selectedRowIndices[i]);
            Variable variable = database.getCompleteVariableData(currentUser, variableSummary);
            selectedVariables.add(variable);
        }
        //XXXX convert summaries into real values
        return selectedVariables;
    }

    public Variable getSelectedVariable() throws MacawException {
        int selectedIndex = searchTable.getSelectedRow();
        if (selectedIndex == -1) {
            return null;
        } else {
            VariableSummary variableSummary = variableSearchTableModel.getVariableSummary(selectedIndex);
            Variable variable = database.getCompleteVariableData(currentUser, variableSummary);
            return variable;
        }
    }

    public void applySearch() {
        String searchPhrase = searchField.getText().trim();
        String selectedCategory = (String) filterByCategory.getSelectedItem();
        String selectedYear = (String) filterByYear.getSelectedItem();
        try {
            if (showRawAndDerivedVariables.isSelected() == true) {
                variableSearchTableModel.filterVariables(currentUser, searchPhrase, selectedYear, selectedCategory);
            } else if (filterByDerivedVariables.isSelected() == true) {
                variableSearchTableModel.filterVariables(currentUser, searchPhrase, selectedYear, selectedCategory, true);
            } else {
                variableSearchTableModel.filterVariables(currentUser, searchPhrase, selectedYear, selectedCategory, false);
            }
        } catch (MacawException exception) {
            Log log = sessionProperties.getLog();
            log.displayErrorDialog(exception);
        }
    }

    public void refreshList() {
        try {
            variableSearchTableModel.refreshList();
        } catch (MacawException exception) {
            Log log = sessionProperties.getLog();
            log.displayErrorDialog(exception);
        }
    }

    public void reset() {
        //reset the search field
        searchField.setText("");
        filterByYear.setSelectedItem(allChoicesText);
        filterByCategory.setSelectedItem(allChoicesText);
        showRawAndDerivedVariables.setSelected(true);
        try {
            variableSearchTableModel.showAllVariableSummaries(currentUser);
        } catch (MacawException exception) {
            Log log = sessionProperties.getLog();
            log.displayErrorDialog(exception);
        }
    }

    public void setChangeListener(ChangeListener changeListener) {
        this.changeListener = changeListener;
    }

    public void setSelectionMode(int selectionMode) {
        ListSelectionModel listSelectionModel = searchTable.getSelectionModel();
        listSelectionModel.setSelectionMode(selectionMode);
    }

    public boolean showRawVariables() {
        if (filterByRawVariables.isSelected() == true) {
            return true;
        } else if (showRawAndDerivedVariables.isSelected() == true) {
            return true;
        } else {
            return false;
        }
    }

    public boolean showDerivedVariables() {
        if (filterByDerivedVariables.isSelected() == true) {
            return true;
        } else if (showRawAndDerivedVariables.isSelected() == true) {
            return true;
        } else {
            return false;
        }
    }

    public void selectVariable(Variable variable) {
        int selectionRow = variableSearchTableModel.getRow(variable.createVariableSummary());
        if (selectionRow != -1) {
            Rectangle selectionArea = searchTable.getCellRect(selectionRow, 0, true);
            searchTable.scrollRectToVisible(selectionArea);
        }
    }

    public boolean hasSearchResults() {
        if (variableSearchTableModel.getRowCount() > 0) {
            return true;
        } else {
            return false;
        }
    }

    // ==========================================
    // Section Errors and Validation
    // ==========================================
    // ==========================================
    // Section Interfaces
    // ==========================================
    //Interface: ActionListener
    public void actionPerformed(ActionEvent event) {
        Object button = event.getSource();
        if (button == applySearch) {
            applySearch();
        } else if (button == reset) {
            reset();
        }
    }

    //Interface: List Selection Listener
    public void valueChanged(ListSelectionEvent event) {
        if ((event.getValueIsAdjusting() == false) && (changeListener != null)) {
            ChangeEvent changeEvent = new ChangeEvent(this);
            changeListener.stateChanged(changeEvent);
        }
    }

    //Interface: Table Model Listener
    public void tableChanged(TableModelEvent event) {
        if (changeListener != null) {
            ChangeEvent changeEvent = new ChangeEvent(this);
            changeListener.stateChanged(changeEvent);
        }
    }
    // ==========================================
    // Section Overload
    // ==========================================
}

