        ArrayList<RawVariable> selectedRawVariables = new ArrayList<RawVariable>();
        ArrayList<DerivedVariable> selectedDerivedVariables = new ArrayList<DerivedVariable>();
        int[] selectedRowIndices = searchTable.getSelectedRows();
        for (int i = 0; i < selectedRowIndices.length; i++) {
            VariableSummary variableSummary = variableSearchTableModel.getVariableSummary(selectedRowIndices[i]);
            Variable selectedVariable = database.getCompleteVariableData(currentUser, variableSummary);
            if (selectedVariable instanceof RawVariable) {
                selectedRawVariables.add((RawVariable) selectedVariable);
            } else {
                selectedDerivedVariables.add((DerivedVariable) selectedVariable);
            }
        }
        database.deleteRawVariables(currentUser, selectedRawVariables);
        database.deleteDerivedVariables(currentUser, selectedDerivedVariables);
        searchTable.clearSelection();
    }

