        int selectedIndex = searchTable.getSelectedRow();
        if (selectedIndex == -1) {
            return null;
        } else {
            VariableSummary variableSummary = variableSearchTableModel.getVariableSummary(selectedIndex);
            Variable variable = database.getCompleteVariableData(currentUser, variableSummary);
            return variable;
        }
    }

