    private SessionProperties sessionProperties;

    private UserInterfaceFactory userInterfaceFactory;

    private VariableSearchPanel variableSearchPanel;

    private JDialog dialog;

    private JButton ok;

    private JButton cancel;

    private boolean isCancelled;

    private Log log;

    // ==========================================
    // Section Construction
    // ==========================================
    public VariableSelectionDialog(SessionProperties sessionProperties) {
        this.sessionProperties = sessionProperties;
        this.userInterfaceFactory = sessionProperties.getUserInterfaceFactory();
        dialog = userInterfaceFactory.createDialog();
        String dialogTitle = MacawMessages.getMessage("variableSelectionDialog.title");
        dialog.setTitle(dialogTitle);
        JPanel panel = userInterfaceFactory.createPanel();
        GridBagConstraints panelGC = userInterfaceFactory.createGridBagConstraints();
        panelGC.fill = GridBagConstraints.BOTH;
        panelGC.weightx = 1.0;
        panelGC.weighty = 1.0;
        variableSearchPanel = new VariableSearchPanel(sessionProperties);
        panel.add(variableSearchPanel.getPanel(), panelGC);
        panelGC.gridy++;
        panelGC.fill = GridBagConstraints.NONE;
        panelGC.weightx = 0;
        panelGC.weighty = 0;
        panelGC.anchor = GridBagConstraints.SOUTHEAST;
        panel.add(createButtonPanel(), panelGC);
        dialog.getContentPane().add(panel);
        dialog.setModal(true);
        dialog.setSize(300, 300);
        isCancelled = false;
        this.log = sessionProperties.getLog();
    }

    private JPanel createButtonPanel() {
        JPanel panel = userInterfaceFactory.createPanel();
        GridBagConstraints panelGC = userInterfaceFactory.createGridBagConstraints();
        panelGC.anchor = GridBagConstraints.SOUTHEAST;
        String okText = MacawMessages.getMessage("general.buttons.ok");
        ok = userInterfaceFactory.createButton(okText);
        ok.addActionListener(this);
        panel.add(ok, panelGC);
        panelGC.gridx++;
        String cancelText = MacawMessages.getMessage("general.buttons.cancel");
        cancel = userInterfaceFactory.createButton(cancelText);
        cancel.addActionListener(this);
        panel.add(cancel, panelGC);
        return panel;
    }

    // ==========================================
    // Section Accessors and Mutators
    // ==========================================
    public Variable getAlternativeVariable() {
        try {
            Variable selectedVariable = variableSearchPanel.getSelectedVariable();
            return selectedVariable;
        } catch (MacawException exception) {
            log.displayErrorDialog(exception);
            return null;
        }
    }

    public void show() {
        dialog.setVisible(true);
    }

    private void ok() {
        dialog.setVisible(false);
    }

    private void cancel() {
        isCancelled = true;
        dialog.setVisible(false);
    }

    public boolean isCancelled() {
        return isCancelled;
    }

    // ==========================================
    // Section Errors and Validation
    // ==========================================
    // ==========================================
    // Section Interfaces
    // ==========================================
    //Interface: ActionListener
    public void actionPerformed(ActionEvent event) {
        Object button = event.getSource();
        if (button == ok) {
            ok();
        } else if (button == cancel) {
            cancel();
        }
    }
    // ==========================================
    // Section Overload
    // ==========================================
}

