                        tableS.setValueAt(ts.getValueAt(i, j), i, j);
                    }
                    for (int j = indice + 2; j < nbColonnes; j++) {
                        tableS.setValueAt(ts.getValueAt(i, j - 1), i, j);
                    }
                }
