                val1 = val1.concat(String.valueOf(val.charAt(k)));
                k++;
            }
            if (k == (val.length() - 1)) {
                // le code de separation n'est pas 160
                val1 = new String();
                k = 0;
                while ((int) val.charAt(k) != 32 && k != (val.length() - 1)) {
