                String colonne = ts.getColumnName(indice);
                String val1Colonne, val2Colonne;
                String[] resNP = colonne.split(" ", 2);
                val1Colonne = resNP[0];
                if (resNP.length > 1)
                    val2Colonne = resNP[1];
