    public TableStockage fusionnerColonnes(int indice) {
        if (nbColonnes > 1) {
            // on ne peut fusionner des objets de type Boolean
            if ((!ts.getColumnClass(indice).equals(Boolean.class)) && (!ts.getColumnClass(indice + 1).equals(Boolean.class))) {
                this.nbColonnes--;
