    public static DaoFactory getDaoFactory() throws DaoFactoryException {
        switch(typeFabrique) {
            case ORACLE:
                return new OracleDaoFactory();
            case DB4O:
                return new Db4oDaoFactory();
            default:
                throw new DaoFactoryException();
        }
