    }

    public boolean setTotalMoney(int money, boolean add) {
        if (!add && getTotalMoney() - money < 0) {
            return false;
        }
        if (!add) {
            this.totalMoney -= money;
            return true;
        }
        if (add) {
            this.totalMoney += money;
            return true;
        }
        return false;
    }

