        urlConnection.connect();
        DataInputStream in = new DataInputStream(urlConnection.getInputStream());
        ByteArrayOutputStream bo = new ByteArrayOutputStream();
        byte[] buffer = new byte[100];
        do {
            int available = in.read(buffer);
            if (available == -1)
