                joxIn = new JOXBeanInputStream(fileIn);
                productInfo = (ProductInfo) joxIn.readObject(ProductInfo.class);
            } else {
                //              log.debug("Error no fileInput");
                productInfo = null;
            }
        } catch (Exception exc) {
            exc.printStackTrace();
        }
        return productInfo;
    }

