        try {
            FileInputStream fileIn = fileUtil.fetchBlendedSearchFile(searchTerm, type);
            if (fileIn != null) {
                joxIn = new JOXBeanInputStream(fileIn);
                testBean = (BlendedSearch) joxIn.readObject(BlendedSearch.class);
            } else {
                //           log.debug("Error no fileInput");
                testBean = null;
            }
        } catch (Exception exc) {
            exc.printStackTrace();
        }
        return testBean;
    }

