        output = transformer.transform(input, source, destination, destinationMessage);
        if (output != null) {
            if (output instanceof String) {
                ((TextMessage) destinationMessage).setRequestText(output.toString(), destination);
            } else if (output instanceof Document) {
                ((XMLMessage) destinationMessage).setRequestDocument((Document) output, destination);
            } else {
                ((ObjectMessage) destinationMessage).setRequestObject(output, destination);
            }
        }
        return destinationMessage;
    }

    static private Message createMessage(Message source, XBUSSystem destination) throws XException {
        /*
		 * Constructing the parameters for the constructor.
		 */
        Class[] conArgsClass = new Class[] { ReflectionSupport.classForName("java.lang.String"), ReflectionSupport.classForName("net.sf.xbus.base.xbussystem.XBUSSystem"), ReflectionSupport.classForName("java.lang.String") };
        Object[] conArgs = new Object[] { source.getFunction(), source.getSource(), source.getId() };
