                                throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_PROTOCOL, Constants.PACKAGE_PROTOCOL_RECORDS, "131", params);
                            } else // if (interfaceContentClass ==
                            // Constants.IFCONTENTCLASS_STRING)
                            {
