                                start = signBytes.length;
                                for (int i = 0; i < start; i++) ((byte[]) buffer)[pos + i] = signBytes[i];
                            }
                            value = value.substring(1).trim();
                        }
                        // if (sign=='+' || sign=='-')
                    }
                    // if (value.length()>0)
                    // Look for a decimal point
                    String decimalPoint = fieldSpec.getAttribute("DecimalPoint");
                    int decPointPos = -1;
                    if (decimalPoint != null && decimalPoint.length() > 0) {
                        char decimalP = '.';
                        if (decimalPoint.equals("comma"))
                            decimalP = ',';
                        else if (!decimalPoint.equals("dot")) {
