                        // Check digits behind decimal point
                        onlyZeros = true;
                        for (int i = value.length() - 1; i > decPointPos; i--) {
                            if (!Character.isDigit(value.charAt(i))) {
                                List params = new Vector();
                                params.add(((Element) fieldSpec.getParentNode()).getAttribute("Name"));
                                params.add(fieldSpec.getAttribute("Name"));
                                params.add("?");
                                throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_PROTOCOL, Constants.PACKAGE_PROTOCOL_RECORDS, "108", params);
                            }
