                            // (!Character.isDigit(valueWithoutSign.charAt(i)))
                            if (onlyZeros && value.charAt(i) == '0')
                                zeroEnd = i;
                            else
                                onlyZeros = false;
                        }
                        // for (int i=valueWithoutSign.length()-1;
                        // i>decPointPos; i--)
                    } else
