                                params.add(fieldSpec.getAttribute("Name"));
                                params.add("?");
                                params.add(value);
                                throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_PROTOCOL, Constants.PACKAGE_PROTOCOL_RECORDS, "108", params);
