            } else // then (fieldFormat.equals("blank"))
            {
                // Not just filler
                String fieldName = fieldSpec.getAttribute("Name");
                String nodeName = null;
                if (fieldNode[0] != null)
                    nodeName = fieldNode[0].getNodeName();
                if (fieldName == null) {
