                    if (interfaceContentClass == Constants.IFCONTENTCLASS_STRING) {
                        ((StringBuffer) buffer).insert(pos, value);
                        // Fill in blanks if the value is to short.
                        for (int i = 0; i < length - value.length(); i++) ((StringBuffer) buffer).insert(pos + value.length() + i, ' ');
                    } else
                        fillByteArray((byte[]) buffer, pos, length, value);
                } else // then (fieldFormat.equals("alpha"))
                if (fieldFormat.equals("num")) {
                    // First look for a leading sign
                    int start = 0;
                    if (value.length() > 0) {
                        char sign = value.charAt(0);
                        if (sign == '+' || sign == '-') {
                            if (interfaceContentClass == Constants.IFCONTENTCLASS_STRING) {
                                ((StringBuffer) buffer).insert(pos, sign);
                                start = 1;
                            } else {
                                byte[] signBytes = byteArrayConverter.stringToByteArray("" + sign);
                                start = signBytes.length;
                                for (int i = 0; i < start; i++) ((byte[]) buffer)[pos + i] = signBytes[i];
                            }
                            value = value.substring(1).trim();
                        }
                        // if (sign=='+' || sign=='-')
                    }
                    // if (value.length()>0)
                    // Look for a decimal point
                    String decimalPoint = fieldSpec.getAttribute("DecimalPoint");
                    int decPointPos = -1;
                    if (decimalPoint != null && decimalPoint.length() > 0) {
                        char decimalP = '.';
                        if (decimalPoint.equals("comma"))
                            decimalP = ',';
                        else if (!decimalPoint.equals("dot")) {
                            List params = new Vector();
                            params.add(((Element) fieldSpec.getParentNode()).getAttribute("Name"));
                            params.add(fieldSpec.getAttribute("Name"));
                            throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_PROTOCOL, Constants.PACKAGE_PROTOCOL_RECORDS, "107", params);
