                        // i>decPointPos; i--)
                    } else
                        // then (decPointPos>-1)
                        // no decimal point
                        for (int i = 0; i < value.length(); i++) {
                            if (!Character.isDigit(value.charAt(i))) {
                                List params = new Vector();
