                            // but only to that extend that the necessary length
                            // is achieved by delelting leading zeros.
                            value = value.substring(0, length - start + zeroBegin + 1);
                        if (zeroBegin > -1)
                            // Delete zeros at beginning
                            value = value.substring(zeroBegin + 1);
