            return pos + length;
        }// try
         catch (NumberFormatException e) {
            throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_PROTOCOL, Constants.PACKAGE_PROTOCOL_RECORDS, "0", e);
        }
        // catch
    }

    // serializeField(Element fieldSpec,StringBuffer buffer,int pos,Node[]
    // fieldNode)
    /**
     * Method fillByteArray.
     *
     * @param b
     * @param pos
     * @param length
     * @param constValue
     */
    private void fillByteArray(byte[] arr, int pos, int length, String value) throws XException {
        byte[] fill = byteArrayConverter.stringToByteArray(value, length);
        for (int i = 0; i < length; i++) arr[pos + i] = fill[i];
    }

    /**
     * Serializes one record.
     *
     * @param doc
     * @return a byte array containing one record
     */
    public byte[] serializeSingleRecord(Document doc) throws XException {
        Element record = doc.getDocumentElement();
        return (byte[]) serializeFieldsAndGroups(record, getRecordTypeSpec(record.getNodeName()));
    }
}
