                        int valueLength = value.length();
                        byte[] valueBytes = null;
                        if (interfaceContentClass == Constants.IFCONTENTCLASS_BYTEARRAYLIST) {
                            valueBytes = byteArrayConverter.stringToByteArray(value);
                            valueLength = valueBytes.length;
                        }
                        if (length != valueLength) {
                            // Otherwise length must be correct.
                            List params = new Vector();
                            params.add(((Element) fieldSpec.getParentNode()).getAttribute("Name"));
                            params.add(fieldSpec.getAttribute("Name"));
                            params.add(value);
                            throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_PROTOCOL, Constants.PACKAGE_PROTOCOL_RECORDS, "138", params);
                        }
                        if (interfaceContentClass == Constants.IFCONTENTCLASS_STRING)
                            ((StringBuffer) buffer).insert(pos, value);
                        else
                            for (int i = 0; i < length; i++) ((byte[]) buffer)[pos + i] = valueBytes[i];
                    }
