                    List params = new Vector();
                    params.add(((Element) fieldSpec.getParentNode()).getAttribute("Name"));
                    params.add(fieldFormat);
                    throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_PROTOCOL, Constants.PACKAGE_PROTOCOL_RECORDS, "139", params);
