             catch (UnsupportedEncodingException e) {
                throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_PROTOCOL, Constants.PACKAGE_PROTOCOL_SIMPLE, "0", e);
            }
            // catch
        }
        // (configEncoding == null)
        return text;
    }
