     * type.
     *
     * @param force
     *            Forces the connection to be opened
     * @throws XException
     *             if something goes wrong
     */
    private void open(boolean force) throws XException {
        if ((!mOpen) || force) {
            mFTPClient = new FTPClient();
            /*
			 * Connect to the FTP Server
			 */
            Configuration config = Configuration.getInstance();
            mHost = config.getValue(CHAPTER_FTPCONNECTION, mName, "Host");
            mPort = config.getValueAsIntOptional(CHAPTER_FTPCONNECTION, mName, "Port");
            try {
                if (mPort > 0) {
                    mFTPClient.connect(mHost, mPort);
                } else {
                    mFTPClient.connect(mHost);
                }
                int reply = getReplyCode();
                if (!FTPReply.isPositiveCompletion(reply)) {
                    close();
                    Vector params = new Vector(2);
                    params.add(mHost);
                    params.add(getReplyString());
                    throw new XException(Constants.LOCATION_EXTERN, Constants.LAYER_TECHNICAL, Constants.PACKAGE_TECHNICAL_FTP, "1", params);
                }
