     * Closes the connection to the FTP server
     */
    public void close() {
        if (mOpen) {
            try {
                if (mFTPClient != null) {
                    mFTPClient.disconnect();
                }
            } catch (Exception e) {
                Trace.warn("Cannot disconnect from FTP server for connection " + mName);
            }
        }
        mOpen = false;
    }

