            boolean successful = mFTPClient.retrieveFile(fileName, outStream);
            if (!successful) {
                outStream.close();
                Vector params = new Vector(2);
                params.add(fileName);
                params.add(getReplyString());
                throw new XException(Constants.LOCATION_EXTERN, Constants.LAYER_TECHNICAL, Constants.PACKAGE_TECHNICAL_FTP, "5", params);
            }
            String retString = outStream.toString(encoding);
            outStream.close();
            return retString;
        } catch (Throwable e) {
            throw new XException(Constants.LOCATION_EXTERN, Constants.LAYER_TECHNICAL, Constants.PACKAGE_TECHNICAL_FTP, "0", e);
        }
    }

    /**
     * Changes the working directory used for file operations.
     *
     * @param workDir
     *            name of the new working directory
     * @throws XException
     *             if something goes wrong
     */
    private void changeWorkDirInternal(String workDir) throws FTPException, IOException {
        if (!mFTPClient.changeWorkingDirectory(workDir)) {
            throw new FTPException();
        }
    }

    /**
     * Changes the working directory used for file operations.
     *
     * @param workDir
     *            name of the new working directory
     * @throws XException
     *             if something goes wrong
     */
    private void changeWorkDir(String workDir) throws XException {
        try {
            if (workDir != null) {
                changeWorkDirInternal(workDir);
            }
        } catch (Exception e1) {
            try {
