				 */
                Trace.warn("FTP connection may be gone, will try a second time");
                open(true);
                changeWorkDirInternal(workDir);
            } catch (FTPException e) {
                Vector params = new Vector(1);
                params.add(workDir);
                throw new XException(Constants.LOCATION_EXTERN, Constants.LAYER_TECHNICAL, Constants.PACKAGE_TECHNICAL_FTP, "12", params);
            } catch (IOException e) {
