            boolean successful = mFTPClient.storeFile(fileName, inStream);
            inStream.close();
            if (!successful) {
                Vector params = new Vector(2);
                params.add(fileName);
                params.add(getReplyString());
                throw new XException(Constants.LOCATION_EXTERN, Constants.LAYER_TECHNICAL, Constants.PACKAGE_TECHNICAL_FTP, "9", params);
