		 */
        if (mAddresses.containsKey(ADDITIONAL_ADDRESS)) {
            AdditionalAddress addAddress = getAdditionalAddressImplementation(getName());
            if (addAddress.hasMarker(text)) {
                // The string contains a marker for additional address
                // information.
                if (mBroadcast) {
                    // For broadcast construct one string for each addiotinal
                    // address.
                    List addAddresses = addAddress.getAddresses();
                    int quantAddresses = addAddresses.size();
                    if (quantAddresses < 1) {
                        // No additional addresses specified in configuration.
                        List params = new Vector();
                        params.add(mName);
                        params.add(text);
                        throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_BASE, Constants.PACKAGE_BASE_XBUSSYSTEM, "5", params);
                    }
                    // if (quantAddresses<1)
                    String[] result = new String[quantAddresses];
                    for (int i = 0; i < quantAddresses; i++) result[i] = addAddress.replaceMarker(text, (String) addAddresses.get(i));
                    return result;
                } else // then (mBroadcast)
                {
                    // Simple addtional address without broadcast.
                    String[] result = { addAddress.replaceMarker(text, (String) mAddresses.get(ADDITIONAL_ADDRESS)) };
                    return result;
                }
                // else (mBroadcast)
            } else // then addAddress.hasMarker(text)
            {
                // No addtional address marker.
                String[] result = { text };
                return result;
            }
            // else (addAddress.hasMarker(text))
        } else // then (mAddresses.containsKey(ADDITIONAL_ADDRESS))
        {
            // No addtional address at all.
            String[] result = { text };
            return result;
        }
        // else (mAddresses.containsKey(ADDITIONAL_ADDRESS))
    }

