                            if (variablesKeySet == null) {
                                throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_BASE, Constants.PACKAGE_BASE_XBUSSYSTEM, "4");
                            }
                            String variablesKey = null;
                            for (Iterator it = variablesKeySet.iterator(); it.hasNext(); ) {
                                variablesKey = (String) it.next();
                                variable = (String) variables.get(variablesKey);
                                if (value.indexOf(variablesKey) >= 0) {
                                    value = XStringSupport.replaceAll(value, variablesKey, variable);
                                }
