    public static boolean isValidTagName(String name) {
        boolean result = (name.length() > 0 && (Character.isLetter(name.charAt(0)) || name.charAt(0) == '_'));
        for (int i = 1; i < name.length() && result; i++) result = (Character.isLetter(name.charAt(i)) || Character.isDigit(name.charAt(i)) || name.charAt(i) == '_');
