    /**
     * Parses the given XML string.
     */
    static public Document parseXML(String xml, String messageName, String system) throws XException {
        Document retDocument = null;
        if (messageName == null) {
            messageName = "Default";
        }
        if ((xml != null) && (xml.length() > 0)) {
            try {
                ByteArrayInputStream xmlStream = new ByteArrayInputStream(xml.getBytes(Constants.getXMLEncoding()));
                InputSource inputSource = new InputSource(xmlStream);
                String systemString = new StringBuffer().append(Constants.XBUS_ETC).append("dtd").append(Constants.FILE_SEPERATOR).toString();
                String systemId = new File(systemString).toURL().toString();
                inputSource.setSystemId(systemId);
                DOMParser parser = new DOMParser();
                parser.setFeature("http://xml.org/sax/features/validation", getValidating(messageName, system));
                parser.setFeature("http://xml.org/sax/features/namespaces", getNamespaceAware(messageName, system));
                String schema = getXMLSchema(messageName, system);
                if (schema != null) {
                    parser.setFeature("http://xml.org/sax/features/namespaces", true);
                    parser.setFeature("http://xml.org/sax/features/validation", true);
                    parser.setFeature("http://apache.org/xml/features/validation/schema", true);
                    parser.setProperty("http://apache.org/xml/properties/schema/external-noNamespaceSchemaLocation", new StringBuffer().append(Constants.XBUS_ETC).append("Schema").append(Constants.FILE_SEPERATOR).append(schema).toString());
                }
                parser.setErrorHandler(new XParserErrorHandler());
                parser.parse(inputSource);
                retDocument = parser.getDocument();
            } catch (Exception e) {
                throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_COREBASE, Constants.PACKAGE_COREBASE_XML, "0", e);
            }
        }
        return retDocument;
    }

    /**
     * Returns a <code>DocumentBuilder</code> needed for parsing. For better
     * performance, <code>DocumentBuilders</code> are cached per thread. Due
     * to the message type the parser may be instructed
     * <ul>
     * <li>to ignore whitespace in elements,</li>
     * <li>to ignore comments,</li>
     * <li>to validate the xml document against its DTD.</li>
     * </ul>
     * Configuration keys control these options.
     *
     * @return a {@link javax.xml.parsers.DocumentBuilder DocumentBuilder}
     *         object for xml parsing
     * @throws XException
     *             in case that any problem with instantiating the
     *             <code>DocumentBuilder</code> object arises
     */
    static public DocumentBuilder getDocumentBuilder(String messageName, String system) throws XException {
        DocumentBuilder docBuilder;
        if (messageName == null) {
            throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_COREBASE, Constants.PACKAGE_COREBASE_XML, "2");
        }
        try // to cast to XException
        {
            DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
            factory.setIgnoringComments(getIgnoringComments(messageName, system));
            factory.setIgnoringElementContentWhitespace(getIgnoringElementContentWhitespace(messageName, system));
            factory.setValidating(getValidating(messageName, system));
            factory.setNamespaceAware(getNamespaceAware(messageName, system));
            docBuilder = factory.newDocumentBuilder();
            docBuilder.setErrorHandler(new XParserErrorHandler());
        }// try
         catch (ParserConfigurationException e) {
            throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_COREBASE, Constants.PACKAGE_COREBASE_XML, "0", e);
        }
        // catch
        return docBuilder;
    }

    // getDocumentBuilder()
    private static boolean getIgnoringComments(String messageName, String system) throws XException {
        Configuration standConf = Configuration.getInstance();
        Configuration xbusConf = Configuration.getInstance("xbus");
        boolean setIgnoringComments = xbusConf.getValueAsBoolean("ParserSettings", messageName, "IgnoringComments");
        if (system != null) {
            setIgnoringComments = setIgnoringComments || standConf.getValueAsBooleanOptional(Constants.CHAPTER_SYSTEM, system, "IgnoringComments");
        }
        return setIgnoringComments;
    }

    private static boolean getIgnoringElementContentWhitespace(String messageName, String system) throws XException {
        Configuration standConf = Configuration.getInstance();
        Configuration xbusConf = Configuration.getInstance("xbus");
        boolean setIgnoringElementContentWhitespace = xbusConf.getValueAsBoolean("ParserSettings", messageName, "IgnoringElementContentWhitespace");
        if (system != null) {
            setIgnoringElementContentWhitespace = setIgnoringElementContentWhitespace || standConf.getValueAsBooleanOptional(Constants.CHAPTER_SYSTEM, system, "IgnoringElementContentWhitespace");
        }
        return setIgnoringElementContentWhitespace;
    }

    private static boolean getValidating(String messageName, String system) throws XException {
        Configuration standConf = Configuration.getInstance();
        Configuration xbusConf = Configuration.getInstance("xbus");
        boolean setValidating = xbusConf.getValueAsBoolean("ParserSettings", messageName, "XMLValidating");
        if (system != null) {
            setValidating = setValidating || standConf.getValueAsBooleanOptional(Constants.CHAPTER_SYSTEM, system, "XMLValidating");
        }
        return setValidating;
    }

    private static String getXMLSchema(String messageName, String system) throws XException {
        Configuration standConf = Configuration.getInstance();
        Configuration xbusConf = Configuration.getInstance("xbus");
        String schema = null;
        if (system != null) {
            schema = standConf.getValueOptional(Constants.CHAPTER_SYSTEM, system, "XMLSchema");
        }
        if (schema == null) {
            schema = xbusConf.getValueOptional("ParserSettings", messageName, "XMLSchema");
        }
        return schema;
    }

    private static boolean getNamespaceAware(String messageName, String system) throws XException {
        Configuration standConf = Configuration.getInstance();
        Configuration xbusConf = Configuration.getInstance("xbus");
        boolean setNamespaceAware = xbusConf.getValueAsBoolean("ParserSettings", messageName, "NamespaceAware");
        if (system != null) {
            setNamespaceAware = setNamespaceAware || standConf.getValueAsBooleanOptional(Constants.CHAPTER_SYSTEM, system, "NamespaceAware");
        }
        return setNamespaceAware;
    }

    /**
     * Serializes a XML document.
     *
     * @param doc
     *            the given <code>org.w3c.dom.Document</code> to be serialized
     * @param systemID
     *            reference to the DTD of the XML document
     * @return the content of the XML document in its string representation
     */
    static public String serializeXML(Document doc, String systemID) throws XException {
        String xmlData = null;
        if (doc != null) {
            Transformer serializer = getSerializer(systemID);
            ByteArrayOutputStream outStream = new ByteArrayOutputStream();
            try {
                serializer.transform(new DOMSource(doc), new StreamResult(outStream));
                xmlData = outStream.toString(Constants.getXMLEncoding());
                outStream.close();
                // The serializer contains a bug and replaces DOS line breaks
                // "\r\n" by "\r\r\n" within any element text.
                // This is corrected in the follwing instruction.
                xmlData = xmlData.replaceAll("\r\r\n", "\r\n");
            } catch (TransformerException e) {
                throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_COREBASE, Constants.PACKAGE_COREBASE_XML, "0", e);
            } catch (IOException e) {
                throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_COREBASE, Constants.PACKAGE_COREBASE_XML, "0", e);
            }
        }
        return xmlData;
    }

    /**
     * Returns a <code>Serializer</code> needed for serializing XML documents.
     */
    static private Transformer getSerializer(String systemID) throws XException {
        setTransformerProperties();
        Transformer serializer;
        TransformerFactory tfactory = TransformerFactory.newInstance();
        // This creates a transformer that does a simple identity transform,
        // and thus can be used for all intents and purposes as a serializer.
        try {
            serializer = tfactory.newTransformer();
            serializer.setOutputProperty(OutputKeys.INDENT, "yes");
            serializer.setOutputProperty(OutputKeys.METHOD, "xml");
            serializer.setOutputProperty(OutputKeys.ENCODING, Constants.getXMLEncoding());
            if ((systemID != null) && (systemID.length() > 0)) {
                serializer.setOutputProperty(OutputKeys.DOCTYPE_SYSTEM, systemID);
            }
        } catch (Throwable e) {
            throw new XException(Constants.LOCATION_INTERN, Constants.LAYER_COREBASE, Constants.PACKAGE_COREBASE_XML, "0", e);
        }
        return serializer;
    }

    /**
     */
    public static void setTransformerProperties() throws XException {
        if (System.getProperty("java.version").startsWith("1.5")) {
            System.setProperty("javax.xml.transform.TransformerFactory", "com.sun.org.apache.xalan.internal.xsltc.trax.TransformerFactoryImpl");
        }
        String transformerProperty = Configuration.getInstance().getValueOptional(Constants.CHAPTER_BASE, "XML", "TransformerFactory");
        if (transformerProperty != null) {
            System.setProperty("javax.xml.transform.TransformerFactory", transformerProperty);
        }
    }
}
